import { ProductActionTypes } from './product.actions';
import { createFeatureSelector, createSelector } from '@ngrx/store'; // identifying the initial state of products

const initialState = {
  showProductCode: true,
  currentProduct: null,
  products: [],
  error: ''
}; // create the main feature selector

const getProductFeatureState = createFeatureSelector('products'); // create the selectors needed for each slice of the state of products needed individually

export const getShowProductCode = createSelector(getProductFeatureState, state => state.showProductCode);
export const getCurrentProduct = createSelector(getProductFeatureState, state => state.currentProduct);
export const getProducts = createSelector(getProductFeatureState, state => state.products); // create the reducer function with strong typing by initial state and the interface of the return state

export function reducer(state = initialState, action) {
  switch (action.type) {
    case ProductActionTypes.ToggleProductCode:
      return Object.assign(Object.assign({}, state), {
        showProductCode: action.payload
      });

    case ProductActionTypes.SetCurrentProduct:
      return Object.assign(Object.assign({}, state), {
        currentProduct: Object.assign({}, action.payload)
      });

    case ProductActionTypes.ClearCurrentProduct:
      return Object.assign(Object.assign({}, state), {
        currentProduct: null
      });

    case ProductActionTypes.InitializeCurrentProduct:
      return Object.assign(Object.assign({}, state), {
        currentProduct: {
          id: 0,
          productName: '',
          productCode: '',
          description: '',
          starRating: 0
        }
      });

    case ProductActionTypes.LoadProductsSuccess:
      return Object.assign(Object.assign({}, state), {
        products: action.payload
      });

    default:
      return state;
  }
}
